package com.jingxc.study.service;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.stereotype.Component;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import com.jingxc.study.config.DeptClientServiceFallbackFactory;@Component@FeignClient(value = "cloud-provider-gesoten",fallbackFactory = DeptClientServiceFallbackFactory.class)public interface FeignConsumerService {    @GetMapping("/test/feign/{id}")    String providerFe(@PathVariable("id") Integer id);    @GetMapping("/test/hystrix/{id}")    String providerHystrix(@PathVariable("id")Integer id);}