package study.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;import study.service.FeignConsumerService;import javax.annotation.Resource;@RestControllerpublic class FeignConsumerController {    @Autowired    private FeignConsumerService feignConsumerService;    @GetMapping("/consumer/feign/{id}")    public String consumerNa(@PathVariable Integer id){        return feignConsumerService.providerFe(id);    }    @GetMapping("/consumer/hystrix/{id}")    public String consumerHystrix(@PathVariable Integer id){        System.out.println(System.currentTimeMillis()/1000);        return feignConsumerService.providerHystrix(id);    }}