package study.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;import javax.annotation.Resource;@RestControllerpublic class NacosConsumerController {    @Value("${server.port}")    private String serverPort;    public static final String INVOKE_URL = "http://cloud-provider-gesoten";    @Resource    private RestTemplate restTemplate;    @GetMapping("/consumer/test/nacos/{id}")    public String consumerNa(@PathVariable Integer id){        String result = restTemplate.getForObject(INVOKE_URL + "/test/nacos/"+id,String.class);        return result;    }}