package com.jingxc.study.config;import lombok.extern.log4j.Log4j;import lombok.extern.log4j.Log4j2;import org.springframework.cloud.gateway.filter.GatewayFilterChain;import org.springframework.cloud.gateway.filter.GlobalFilter;import org.springframework.core.Ordered;import org.springframework.http.HttpStatus;import org.springframework.stereotype.Component;import org.springframework.web.server.ServerWebExchange;import reactor.core.publisher.Mono;@Log4j2@Componentpublic class TokenFilter implements GlobalFilter {    @Override    public Mono<Void> filter(ServerWebExchange exchange, GatewayFilterChain chain) {        String token = exchange.getRequest().getQueryParams().getFirst("token");        if (token == null || token.isEmpty()) {            log.info( "token is empty..." );            exchange.getResponse().setStatusCode(HttpStatus.UNAUTHORIZED);            return exchange.getResponse().setComplete();        }        return chain.filter(exchange);    }}