package com.jingxc.study.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.UUID;@RestControllerpublic class GesotenProviderController {    @Value("${server.port}")    private String serverPort;    @RequestMapping("/test/zk")    public String providerZk(){        return "springcloud with zookeeper" + serverPort + UUID.randomUUID().toString();    }}